data.custom_css:
body.dashboard-page {
    background: url(https://blog.datalets.ch/content/images/2020/03/IMG_20200306_130350_C.jpg);
    background-size: fill;
}
.project-page .project-info a.btn-large {
    color:white; 
    text-shadow: 1px 1px 2px black; 
}
.with-event .honeycomb {
/* too gaudy - can barely read the challenges now... 
    background: url(https://opendata.ch/wordpress/files/2019/11/Energy-Data-Hackdays-2020.jpg) no-repeat;
    background-size: cover;
    background-position: center; 
*/
}
.challenge.hexagon::before,
.challenge.hexagon::after {
    opacity: 1;
}
.challenge.hexagon {
    font-size: 9.4pt;
    background-image: url(https://opendata.ch/wordpress/files/2020/03/200828-29_Flyer_energyHackdays-1536x1081.png) !important;
    background-size: 482%;
}
.challenge.hexagon .hexagontent {
    font-size: 10pt;
}
.bam-container {
    font-size: 135%; margin: 2em;
}
a.bam {
    font-size: 150%; font-weight: bold; text-decoration: none;
}
#ideas-list .list-group-item {
    width: 100%; height: 2em;
}
.project-info .project-longtext .btn-lg {
    color: white;
}
