data.custom_css:
.hexagon .hexagontent { font-size: 0.8rem; font-weight: normal; }
.embed-event .hexagon { zoom: none !important; }

.project-page .project-info a.btn-large {
    color:white; 
    text-shadow: 1px 1px 2px black; 
}
.project-longtext a.btn { color: white; text-shadow: 1px 1px 2px black; }

.challenge.hexagon::before,
.challenge.hexagon::after {
    opacity: 1;
}
.challenge.hexagon .hexagontent {
    font-size: 10pt;
}
.bam-container {
    font-size: 135%; margin: 2em;
}
a.bam {
    font-size: 150%; font-weight: bold; text-decoration: none;
}
#ideas-list .list-group-item {
    width: 100%; height: 2em;
}

body.dashboard-page {
    background: url(https://opendata.ch/wordpress/files/2020/05/OFH_300dpi-1536x1023.jpg);
    background-size: cover; background-repeat: no-repeat;
}
#announcements { margin: 0px; }
.dashboard-page .event-countdown { margin-top: 150px; }
.dashboard-page .container-fluid .sidebar { background: none !important; }
.dashboard-page .navbar.fixed-top,
.dashboard-page #event-logo, 
.dashboard-page #activities { display: none; }
