data.custom_css:
html body {
  background-color: #fff;
}
.dashboard-page {
  background: white !important;
  alt-background: url(https://opendata.ch/wordpress/files/2023/04/Hackaton23-events-banner.jpg) !important;
}
.dashboard-page #event-logo {
    left: 10%;
}
.dashboard-page .container-fluid .socialwall {
  background: none !important;
}
.dashboard-page .container-fluid .sidebar {
    display: none;
}
.dashboard-page #announcements {
    display: block;
    top: 40%;
    left: 4%;
}
.alert.project-tips.profile-projects,
.dashboard-page #announcements textarea {
    background: #ffeb9d; 
    border-radius: 0px;
    border: 4px dashed white !important;
}
.dashboard-page #activities div {
    background: rgba(230,230,230,0.9) !important;
}
.codeofconduct {
  box-shadow: 0 0 0 4px #71cbcd,0 0 0 6px white,0 0 0 10px #088989;
}
